choose.when:
SELECT c.grid_id AS gridId, gi.grid_name AS stationName, ST_X(gi.center_point) AS latitude, ST_Y(gi.center_point) AS longitude, SUM(IFNULL(c.loan_balance, 0)) AS value FROM grid_customer c INNER JOIN grid_info gi ON c.grid_id = gi.id AND gi.deleted = 0 AND gi.grid_type = 'HUINONG' WHERE c.customer_type = 'HUINONG_LOAN' AND c.deleted = 0 AND gi.center_point IS NOT NULL
AND c.grid_id IN
#{gridId}
AND c.customer_category IN
#{category}
AND gi.grid_marketing_flag = #{reqVO.gridMarketingFlag}
GROUP BY c.grid_id, gi.grid_name, gi.center_point ORDER BY value DESC
